import { useHistory } from "react-router-dom";

const HomePageBox = ({ pic, text }) => {
    const [hoverIng, setHovering] = useState(false)
    console.log("called")
    return ( 

     <div 
        onMouseEnter={(()=>{console.log("hovering" + hoverIng); setHovering(true)})}
        onMouseLeave={(()=>{setHovering(false)})}
        // onMouseOut={(()=>{console.log("no longer hovering");setHovering(false)})}
        className="flex border-2 border-red-900 w-1/3 mx-auto relative z-50 duration-200 cursor-pointer duration-1000">
            <img src={pic} className="relative"></img>
         <p 
         onMouseEnter={(()=>{setHovering(true)})}
         className={`${hoverIng === true ? ` mx-auto bg-black text-red-400 duration-1000 px-8 py-3 text-xl font-semibold`:` text-xl font-semibold px-8 h-1/4 relative -ml-72 bg-white text-red-900`}`}>Recruiting</p>
    </div>      
    )

}
export default HomePageBox;
