import React, { useState, useEffect } from "react";

import { useHistory } from "react-router-dom";
import HoverMenu from "../components/hoverMenu";

const NavOption = ({ text  }) => {
  const menu =[
    {name:"name"}
  ]
// const history = useHistory();
if (text.redirect !== "/about") {
  console.log("not about")
  return ( 
    <>
  <button className="py-2 bg-red-500 mx-1 text-sm cursor-pointer hover:bg-red-800 px-8 text-white duration-200 rounded-sm" onClick={(()=>{window.location = text?.redirect})}>{text?.title}</button>

    </>
)
} else {

  return ( 
    <>
  <button className="py-2 bg-red-500 mx-1 text-sm cursor-pointer hover:bg-red-800 px-8 text-white duration-200 rounded-sm" onMouseEnter={<HoverMenu menu={menu}></HoverMenu>}>{text?.title}</button>

    </>
)
}


}
export default NavOption;
