import React, { useState, useEffect } from "react";

import { useHistory } from "react-router-dom";

const HomePageBox = ({ pic, text, link }) => {
    const [hoverIng, setHovering] = useState(false)
    console.log("called")
    return ( 

     <div 
        onMouseEnter={(()=>{console.log("hovering" + hoverIng); setHovering(true)})}
        onMouseLeave={(()=>{setHovering(false)})}
        onClick={(()=>{window.location = link})}
        // onMouseOut={(()=>{console.log("no longer hovering");setHovering(false)})}
        className="h-1/3 flex border-opacity-70 border-4 hover:border-black mt-20 hover:border-4 border-red-600 w-1/3 ml-3 mr-3 mx-auto relative z-50 duration-200 cursor-pointer duration-1000">
            <img src={pic} className={hoverIng ? " relative opacity-100 duration-1000": " relative opacity-80 duration-1000"}></img>
    <div className="absolute flex w-full text-center">
    <p 
         onMouseEnter={(()=>{setHovering(true)})}
         className={`${hoverIng === true ? ` mx-auto bg-black text-gray-400 duration-1000 px-8 py-3 z-50 text-xl font-semibold border-gray-600 border-b-2 border-l-2 border-r-2`:`bg-opacity-50 border-red-600 border-b-2 border-l-2 border-r-2 text-xl font-semibold px-8 mx-auto h-1/4 bg-white text-red-600 duration-1000`}`}>
        {text}</p>
    </div>

    </div>      
    )

}
export default HomePageBox;
